import ReactDOM from 'react-dom';
import classes from './Modal.module.css';

const portalElement = document.getElementById('overlays');

const Modal = props => {
    return (
        <>
            {ReactDOM.createPortal(<div onClick={props.onClose} className={classes.backdrop}></div>, portalElement)}
            {ReactDOM.createPortal(
                <div className={`${classes.modal} ${props.isVisible ? classes.visible : ''}`}>
                    {props.children}
                </div>,
                portalElement
            )}

        </>
    )
}

export default Modal;
