const AvailableMeals = () => {
    const [meals, setMeals] = useState([]);

    useEffect(() => {
        fetch('https://react-http-efe1d-default-rtdb.firebaseio.com/meals.json')
            .then(res => res.json())
            .then(data => {
                const loadedMeals = [];
                for (const mealKey in data) {
                    loadedMeals.push({
                        id: mealKey,
                        ...data[mealKey]
                    })
                    console.log(loadedMeals);
                }
                setMeals(loadedMeals);
            })
    }, []);

    const listMeals = meals.map(meal =>
        <MealItem
            key={meal.id}
            id={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />)
    return (
        <Card className={classes['meals-list']}>
            <section>
                <ul>
                    {listMeals}
                </ul>
            </section>
        </Card>
    )
}

export default AvailableMeals;
