import { useReducer } from "react";
import CartContext from "./cart-context";

const defaultCartItem = {
    items: [],
    totalAmount: 0
}

const reducerCartItems = (state, action) => {
    if (action.type === 'ADD') {
        const addedCartItemIndex = state.items.findIndex(item => item.id === action.item.id);
        const addedCartItem = state.items[addedCartItemIndex];
        let updatedItems;
        if (addedCartItem) {
            const updatedItem = {
                ...addedCartItem,
                amount: action + addedCartItem.amount
            }
            updatedItems = [...state.items, updatedItem]
        } else {
            updatedItems = state.items.concat(action.item);
        }
        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;
        return {
            items: updatedItems,
            totalAmount: updatedTotalAmount
        }
    }
    return defaultCartItem;
}

const CartProvider = props => {
    const [cartState, dispatchCartAction] = useReducer(reducerCartItems, defaultCartItem);
    const addItemToCartHandler = (item) => {
        dispatchCartAction({type: 'ADD', item: item})
    };
    const removeItemFromCartHandler = (id) => {
        dispatchCartAction({type: 'REMOVE', id: id})
    };
    const cartContext = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemToCartHandler,
        removeItem: removeItemFromCartHandler
    }
    return (
        <CartContext.Provider value={cartContext}>
            {props.children}
        </CartContext.Provider>
    )
}

export default CartProvider;
