import classes from './OrderForm.module.css';
import useInput from '../../hooks/use-input';

const textValidation = (value) => {
    return value.trim() !== ''
}

const OrderForm = props => {
    const {
        value: enteredName,
        isValid: enteredNameIsValid,
        hasError: nameHasError,
        blurHandler: nameBlurHandler,
        valueChangeHandler: nameChangeHandler,
        reset: resetName
    } = useInput(textValidation);

    const confirmHandler = (event) => {
        event.preventDefault();
        if (nameHasError) {
            return;
        }
        console.log(enteredName);
    }

    return (
        <div className={classes['order-form']}>
            <form onSubmit={confirmHandler}>
                <div className={classes.control}>
                    <label htmlFor='name'>Your Name</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        onChange={nameChangeHandler}
                        onBlur={nameBlurHandler}
                    />
                </div>
                <div className={classes.control}>
                    <label htmlFor='phone'>Your Phone</label>
                    <input type="tel" id="phone" name="phone"/>
                </div>
                <div className={classes.control}>
                    <label htmlFor='street'>Street</label>
                    <input type="text" id="street" name="street"/>
                </div>
                <div className={classes.control}>
                    <label htmlFor='code'>Pestal Code</label>
                    <input type="text" id="code" name="code"/>
                </div>
                <div className={classes.actions}>
                    <button className={classes.button}>Confirm</button>
                    <button className={classes['button--alt']} onClick={props.onCancel} type="button">Cancel</button>
                </div>
            </form>
        </div>
    )
};

export default OrderForm
