import { useEffect, useState } from 'react';
import Card from '../UI/Card';
import classes from './AvailableMeals.module.css';
import MealItem from './MealItem/MealItem';
import { ThreeDots } from 'react-loader-spinner';

const AvailableMeals = () => {
    const [meals, setMeals] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchMeals = () => {
        setIsLoading(true);
        fetch('https://react-http-efe1d-default-rtdb.firebaseio.com/meals.json')
            .then(res => {
                if (!res.ok) {
                    throw new Error('Some thing went wrong!');
                }
                return res.json()
            })
            .then(data => {
                const loadedMeals = [];
                for (const mealKey in data) {
                    loadedMeals.push({
                        id: mealKey,
                        ...data[mealKey]
                    })
                }
                setIsLoading(false);
                setMeals(loadedMeals);
            })
            .catch(error => {
                setIsLoading(false);
                setError(error.message);
            })
    };

    useEffect(() => {
        fetchMeals();
    }, []);

    let content = <p>Found no meals</p>;

    if (isLoading) {
        content = <ThreeDots
            height="80"
            width="80"
            radius="9"
            color="#4fa94d"
            ariaLabel="three-dots-loading"
            // wrapperClassName={classes.loading}
            visible={true}
        />

        if (meals.length > 0 && !isLoading) {
            content = <ul>{meals.map(meal =>
                <MealItem
                    key={meal.id}
                    id={meal.id}
                    name={meal.name}
                    description={meal.description}
                    price={meal.price}
                />)}</ul>
        }

        if (error) {
            content = <p style={{ textAlign: 'center' }}>{error}</p>
        }

        return (
            <Card className={classes['meals-list']}>
                <section>{content}</section>
            </Card>
        )
    }
}

export default AvailableMeals;
