function App() {
  const [cartIsVisible, setCartIsVisible] = useState(false);

  const showCartHandler = () => {
    setCartIsVisible(true);
  }

  return (
    <>
      <Cart cartIsVisible={cartIsVisible} on />
      <Header />
      <main>
        <Meals />
      </main>
    </>
  );
}

export default App;
